# 例：a(1).txt / a (2).txt / a(12) (3).jpg 都会被删除
PAREN_NUM_IN_STEM = re.compile(r'\(\s*\d+\s*\)')

def iter_files(folder):
    if RECURSIVE:
        for root, _, files in os.walk(folder):
            for f in files:
                yield root, f
    else:
        for f in os.listdir(folder):
            p = os.path.join(folder, f)
            if os.path.isfile(p):
                yield folder, f

deleted, skipped = 0, 0

